import React from 'react';
import { useState, useEffect } from 'react'
import { StyleSheet, Text, View, FlatList, TextInput, TouchableOpacity, Dimensions } from 'react-native';
import { getDatabase, ref, onValue, set } from 'firebase/database';
import Firebase from '../firebaseConfig';

export default function OrdersScreen({navigation}) {
    const [orders, setOrders] = useState([])
    const [order, setOrder] = useState([])

    useEffect(() => {
        const db = getDatabase(Firebase)
        const menuRef = ref(db, 'orders/')
        onValue(menuRef, (snapshot) => {
            //console.log(snapshot.val())
            setOrders([])
            snapshot.forEach((child) => {
                const newObj = {
                    orderId: child.key,
                    order: child.val().order,
                    total: child.val().total
                }
                setOrders(emptyArray => [...emptyArray, newObj])
            })
            //const orderData = snapshot.val()
            //setOrder([])
            //setPreparing(snapshot.val().preparing)
            //setFinished(snapshot.val().finished)
            //setOrder(emptyArray => [...emptyArray, snapshot.val().order])
            /*snapshot.val().order.forEach((child) => {
                const newObj = {
                    type: child.type,
                    size: child.size,
                    price: child.price
                }
                setOrder(emptyArray => [...emptyArray, newObj])
            })*/
        })
    }, [])

    //console.log(orders)
    //console.log(orders[0].order)

    return(
        <View style={styles.screen}>
            <Text style={styles.title}>Orders</Text>
            <View>
                <FlatList
                    data={orders}
                    renderItem={({item}) => {
                        return(
                            <TouchableOpacity
                                style={styles.item}>       
                                <View style = {{flexDirection: 'column'}}>
                                    <Text style={{fontWeight: 'bold', fontSize: 18}}>{item.orderId}</Text>
                                    <View></View>
                                    <Text>Total: {item.total.toFixed(2)}$</Text>
                                </View>
                            </TouchableOpacity>
                        );
                }}/>
            </View>
            <TouchableOpacity onPress={() => navigation.navigate('Menu')}>
                <Text>Cafe menu</Text>
            </TouchableOpacity>
        </View>
    )
}

const styles = StyleSheet.create({
    screen: {
        flex: 1,
        height: Dimensions.get('window').height,
        backgroundColor: 'white',
        alignItems: 'center',
        paddingTop: 60,
        paddingBottom: 80,
        justifyContent: 'center'
    },
    title: {
        fontSize: 30,
        fontWeight: 'bold',
        paddingTop: 20,
        paddingBottom: 10,
        textShadowColor: 'grey',
        textShadowOffset: { width: 0, height: 1 },
        textShadowRadius: 3,
        elevation: 5
    },
    item: {
        alignItems: 'center',
        justifyContent: 'center',
        height: 70,
        width: Dimensions.get('window').width - 20,
        marginBottom: 20,
        borderWidth: 3,
        borderRadius: 70,
        borderColor: 'lightblue',
        borderStyle: 'solid'
    }
})
