import React from 'react';
import { useState, useEffect } from 'react'
import { StyleSheet, Text, View, FlatList, TextInput, TouchableOpacity, Dimensions, Modal } from 'react-native';
import { getDatabase, ref, onValue, set } from 'firebase/database';
import Firebase from '../firebaseConfig';

export default function OrdersScreen({navigation}) {
    const [orders, setOrders] = useState([])
    const [order, setOrder] = useState([])
    const [modalVisible, setModalVisible] = useState(false)

    useEffect(() => {
        const db = getDatabase(Firebase)
        const menuRef = ref(db, 'orders/')
        onValue(menuRef, (snapshot) => {
            //console.log(snapshot.val())
            setOrders([])
            snapshot.forEach((child) => {
                const newObj = {
                    orderId: child.key,
                    order: child.val().order,
                    total: child.val().total,
                    preparing: child.val().preparing,
                    finished: child.val().finished
                }
                setOrders(emptyArray => [...emptyArray, newObj])
            })
            //const orderData = snapshot.val()
            //setOrder([])
            //setPreparing(snapshot.val().preparing)
            //setFinished(snapshot.val().finished)
            //setOrder(emptyArray => [...emptyArray, snapshot.val().order])
            /*snapshot.val().order.forEach((child) => {
                const newObj = {
                    type: child.type,
                    size: child.size,
                    price: child.price
                }
                setOrder(emptyArray => [...emptyArray, newObj])
            })*/
        })
    }, [])

    //console.log(orders)
    //console.log(orders[0].order)

    function getOrderStatus() {
        //
    }

    function openOrder({item}) {
        setOrder(emptyArray => [...emptyArray, item])
        setModalVisible(true)
        //console.log(orders[0].order)
        console.log(order)
    }

    return(
        <View style={styles.screen}>
            <Text style={styles.title}>Orders</Text>
            <View>
                <FlatList
                    data={orders}
                    renderItem={({item}) => {
                        return(
                            <TouchableOpacity
                                style={styles.item}
                                onPress={() => openOrder(item)}>       
                                <View style = {{flexDirection: 'column'}}>
                                    <Text style={{fontWeight: 'bold', fontSize: 18}}>{item.orderId}</Text>
                                    <View></View>
                                    <Text>Total: {item.total.toFixed(2)}$</Text>
                                </View>
                            </TouchableOpacity>
                        );
                }}/>
            </View>
            <TouchableOpacity 
                style={styles.button}
                onPress={() => navigation.navigate('Menu')}>
                <Text>Cafe menu</Text>
            </TouchableOpacity>
            <Modal 
                animationType="none"
                transparent={true}
                visible={modalVisible}
                onRequestClose={() => setModalVisible(!modalVisible)}>
                <View style={styles.modalScreen}>
                    <View style={styles.modalView}>
                        <View>
                            <FlatList
                                data={order}
                                renderItem={({item}) => {
                                    return(
                                        <View>
                                            <View style = {{flexDirection: 'row'}}>
                                                <Text style={{fontWeight: 'bold', fontSize: 15}}>{item.type}</Text>
                                                <Text>Price: {item.price}$</Text>
                                            </View>
                                        </View>
                                    );
                                }}
                            />
                        </View>
                        <View>
                            <Text>Total: {}</Text>
                        </View>
                    </View>
                </View>
            </Modal>
        </View>
    )
}

const styles = StyleSheet.create({
    screen: {
        flex: 1,
        height: Dimensions.get('window').height,
        backgroundColor: 'white',
        alignItems: 'center',
        paddingTop: 100,
        paddingBottom: 100,
        justifyContent: 'center'
    },
    title: {
        fontSize: 30,
        fontWeight: 'bold',
        paddingTop: 20,
        paddingBottom: 10,
        textShadowColor: 'grey',
        textShadowOffset: { width: 0, height: 1 },
        textShadowRadius: 3,
        elevation: 5
    },
    item: {
        alignItems: 'center',
        justifyContent: 'center',
        height: 70,
        width: Dimensions.get('window').width - 20,
        marginBottom: 20,
        borderWidth: 3,
        borderRadius: 70,
        borderColor: 'lightblue',
        borderStyle: 'solid'
    },
    button: {
        borderWidth: 3,
        borderColor: 'lightblue',
        alignItems: 'center',
        justifyContent: 'center',
        paddingHorizontal: 30,
        paddingVertical: 15,
        backgroundColor: 'white',
        borderRadius: 40,
        margin: 20
    },
    modalScreen: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    modalView: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        maxHeight: Dimensions.get('window').height - 60,
        width: Dimensions.get('window').width - 30,
        backgroundColor: 'white',
        borderWidth: 3,
        borderRadius: 50,
        borderColor: "lightblue",
    },
})
